import React, {useState, useEffect} from 'react'
import './principal.css';

const Index = () => {
 
  const [contador, setContador] = useState(0)

  let cambios = contador

  useEffect((contador) =>{

    cambios = contador;

  }, [contador])
 
  const aumentar = () => {
    setContador (contador + 1);
  }

  const disminuir = () => {
    setContador (contador - 1);
  }

  const reset = () => {
    setContador (0);
  }

  
  
  return (
    <>
    <h1 className="titulo">CONTADOR ADSI SENA</h1>

    <div>
      <h1 className="contador">{cambios}</h1>
    </div>
    
    <div className="contenedor-btn">
      <button className="btn-contador" onClick={disminuir}>disminuir</button>
      <button className="btn-contador" onClick={reset}>reset</button>
      <button className="btn-contador" onClick={aumentar}>aumentar</button>
    </div>
    </>
  )
}

export default Index;
